import argparseimport csvparser = argparse.ArgumentParser(add_help=False)parser.add_argument('files', type=str, nargs=2)parser.add_argument('--in-delimiter=', type=str, dest='delimiter', default='|')parser.add_argument('--in-quote=', type=str, default='""')args = parser.parse_args()with open(args.files[0], 'r') as f:    dialect = csv.Sniffer().sniff(f.read(1024))    f.seek(0)    reader = csv.reader(f, delimiter=dialect.delimiter)    with open(args.files[1], 'w') as new_f:        writer = csv.writer(new_f, delimiter=',')        for row in reader:            writer.writerow(row)